for (i = -8; i <= 8; ++i) {
  draw((i,-8)--(i,8),gray(0.7));
  draw((-8,i)--(8,i),gray(0.7));
}

draw((-8,0)--(8,0));
draw((0,-8)--(0,8));
draw((2,-8)--(2,8),dashed);
draw((-8,-1)--(8,-1),dashed);
draw(graph(func,-8,1.9),red);
draw(graph(func,2.1,8),red);

limits((-8,-8),(8,8),Crop);
[/asy]

