class Queue:
    def __init__(self):
        self.queue = []
        self.stack = []

    def enqueue(self, item):
        if item == []:
            self.queue.append(self.stack.pop())
        else:
            self.queue.append(item)
            self.stack.append(self.queue.pop())

    def dequeue(self):
        if self.queue.isEmpty():
