```
#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main()
{
    string filename = "test.txt"; // 文件名
    string word_to_count = "hello"; // 要计算出现次数的单词
    int count = 0; // 出现次数

    ifstream file(filename); // 打开文件
    if (file.is_open()) { // 如果成功打开文件
        string line;
        while (getline(file, line)) { // 逐行读取文件内容
            size_t pos = 0;
            while ((pos = line.find(word_to_count, pos)) != string::npos) { // 在每一行中查找指定单词
                ++count;
                pos += word_to_count.length();
            }
        }
        file.close(); // 关闭文件
    }

    cout << "The word \"" << word_to_count << "\" appears " << count << " times in the file." << endl;

    return 0;
}
```

