public int[] findCommonElements(int[] arr1, int[] arr2) {
    Arrays.sort(arr1);
    Arrays.sort(arr2);

    int i = 0, j = 0;
    int[] res = new int[Math.min(arr1.length, arr2.length)];
    int k = 0;

    while (i < arr1.length && j < arr2.length) {
        if (arr1[i] == arr2[j]) {
            res[k++] = arr1[i];
            i++;
            j++;
        } else if (arr1[i] < arr2[j]) {
            i++;
        } else {
            j++;
        }
    }

    return Arrays.copyOfRange(res, 0, k);
}
