#include <iostream>
#include <fstream>
#include <string>

int main() {
    // open the text file
    std::ifstream file("text.txt");

    // declare variables
    std::string word = "book";
    int count = 0;

    // read the file line by line
    std::string line;
    while (getline(file, line)) {
        // split the line by any whitespaces
        std::istringstream iss(line);
        std::string word_split;
        iss >> word_split;

        // check if the word is equal to the given word
        if (word_split == word) {
            count++;
        }
    }

    // print the result
    std::cout << "The word " << word << " occurs " << count << " times in the text.\n";

    return 0;
}

import re

def count_occurrences(filename, word):
    with open(filename) as f:
        for line in f:
            m = re.search(r'(?:(\b|_)(?=[0-9]+)(?:[a-zA-Z]+))', line)
            if m and m.group() == word:
                return m.group()

print(count_occurrences("text.txt", "book"))  # Output: book occurs 3 times in the text.

