def fibonacci(n): 
  if n<0: 
    print("Incorrect input") 
  elif n==1: 
    return 0
  elif n==2: 
    return 1
  else: 
    return fibonacci(n-1)+fibonacci(n-2)

class Queue:
    def __init__(self):
        self.items = []
        self.front = None
        self.back = None

    def is_empty(self):
        return self.back == None

    def enqueue(self, item):
        if self.is_empty():
            self.front = item
        else:
            self.back = item

    def dequeue(self):
        if not self.is_empty():
            return self.front
        else:
            return self.back

    def peek(self):
        if not self.is_empty():
            return self.front
        else:
            return self.back

class Stack:
    def __init__(self):
        self.items = []
        self.front = None
        self.back = None

    def is_empty(self):
        return self.back == None

    def push(self, item):
        if not self.is_empty():
            self.back = item
        else:
            self.front = item

    def pop(self):
        if not self.is_empty():
            return self.front
        else:
            return self.back

    def peek(self):
        if not self.is_empty():
            return self.front
        else:
