def findCommonSubsequence(str1, str2):
    """
    Returns the longest common substring of two given strings
    Input: str1, str2 (string)
    Output: common_subseq (string)
    """

    # Create two dictionaries to store information
    # about the two strings
    d1 = {}
    d2 = {}

    # Initialize length of both strings
    d1["length"] = 0
    d2["length"] = 0

    # Traverse both strings and update length
    for i in range(max(len(str1), len(str2)):
        if i in d1:
            d1["length"] += 1
        elif i in d2:
            d2["length"] += 1
        else:
            d1[i] = d2[i] = 1

    # Traverse the longest common subsequence
    for i in range(max(len(str1), len(str2)):
        if d1[i] > d2["length"]:
            d2["length"] = d1[i]

    # Return the longest common subsequence
    return d2["length"]

def find_element(arr, target):
    low = 0
    high = len(arr)-1

    while low <= high:
        mid = (low+high) // 2
        if arr[mid] == target:
            return mid
        elif arr[mid] < target:
            low = mid + 1
        else:
            high = mid - 1

    return -1

