```cpp
#include<iostream>
#include<fstream>
using namespace std;
int main()
{
    ifstream file;
    string line;
    int word_count = 0;
    file.open("file.txt");
    while (getline(file, line))
    {
        if (line.find("specific_word") != string::npos)
        {
            word_count++;
        }
    }
    file.close();
    cout << "The number of occurrences of specific word is: " << word_count << endl;
    return 0;
}
```

