class Queue:
    def __init__(self):
        self.items = []
        self.front = None
        self.rear = None

    def is_empty(self):
        return not self.items

    def is_full(self):
        return not self.items or not self.rear

    def enqueue(self, item):
        if not self.is_full():
            self.items.append(item)
            self.rear = self.rear + 1

    def dequeue(self):
        if not self.is_empty():
            return self.items.pop()

    def clear(self):
        self.items = []
        self.rear = None
